mapper.insert:
 		insert into calendar(
		      cal_no     
		    , cal_title   
		    , cal_content
		    , cal_start_ymd
		    , cal_end_ymd
		    , cal_color
		    , ae_id
		 )values( 
		      'CA' || LPAD(SEQ_CA.NEXTVAL, 10, '0')
		    , #{cal_title}
		    , #{cal_content}
		    , #{cal_start_ymd}
		    , #{cal_end_ymd}
		    , #{cal_color}
		    , #{ae_id}   
		 )

mapper.update:
 		update calendar set
		      cal_title = #{cal_title}
		    , cal_content = #{cal_content}
		    , cal_start_ymd = #{cal_start_ymd}
		    , cal_end_ymd = #{cal_end_ymd}
		    , cal_color = #{cal_color}
		 where 
		      cal_no = #{cal_no}

 		update calendar set
		      cal_start_ymd = #{cal_start_ymd}
		    , cal_end_ymd = #{cal_end_ymd}
		 where 
		      cal_no = #{cal_no}
