mapper.update:
update
		com_member
set
	    mem_profile = #{mem_profile}
	  , mem_nicknm = #{mem_nicknm}
where
		ae_id = #{ae_id}

update
	ebook_manage
set
	  em_read_page = #{em_read_page}
where
	  ae_id = #{ae_id}
and
	  book_no = #{book_no}

mapper.insert:
insert 
into 
	ebook_recode(
	  er_page
	, ae_id
	, book_no  
)values(
	  #{em_read_page}
	, #{ae_id}
	, #{book_no}  
)

mapper.delete:
delete
from
	ebook_recode
where
	  ae_id = #{ae_id}
and
	  book_no = #{book_no}
and
	  er_page = #{em_read_page}

mapper.select:
select
	er_page
from
	ebook_recode
where
	  ae_id = #{ae_id}
and
	  book_no = #{book_no}
and
 	  er_page = #{er_page}

select
	em_page
from
	ebook_memo
where
	em_page = #{em_page}
and
	ae_id = #{ae_id}
and
	book_no = #{book_no}

mapper.update:
update
	ebook_memo
set
	em_content = #{em_content}
where
	em_page = #{em_page}
and
	ae_id = #{ae_id}
and
	book_no = #{book_no}

mapper.insert:
insert 
into
	ebook_memo(
	   em_page
	 , ae_id
	 , book_no
	 , em_content
)values(
	  #{em_page}
	, #{ae_id}
	, #{book_no}
	, #{em_content}
)
